INSERT IGNORE INTO "products" ("_id", "title", "isNew", "oldPrice", "price", "type", "description", "image", "rating", "quantity", "ingredients", "position", "category_id") VALUES
	(1, 'Молоко натуральное цельное', 1, 110.0, 100.0, '1л.', 'Наше натуральное цельное молоко — это продукт высшего качества, полученный от здоровых коров, которые питаются только натуральными кормами. Это молоко сохраняет все полезные свойства и витамины, что делает его идеальным выбором для всей семьи. Оно обладает насыщенным вкусом и кремовой текстурой, что делает его отличным дополнением к вашему рациону.', '1.webp', 4, 1, 'Состав: молоко, витамины, полезные свойства.', 1, 2),
	(2, 'Крем творожный с зеленью', 0, 120.0, 100.0, '200гр.', 'Крем творожный с зеленью — это свежий и оригинальный десерт, который сочетает в себе нежность творога и тонкий вкус зелени, создавая уникальный и освежающий вкус.', '17.png', 3, 1, '', NULL, 3),
	(3, 'Кефир', 1, 0.0, 90.0, '0,5л', 'Откройте для себя уникальный вкус и пользу нашего кефира, изготовленного из свежего молока, полученного от лучших фермеров Рязанской области. Этот продукт — идеальный выбор для тех, кто стремится к здоровому образу жизни и ценит натуральные ингредиенты.', '3.webp', 3, 2, 'Состав: кефир, молоко, закваска.', NULL, 2),
	(4, 'Сыворотка', 0, 60.0, 60.0, '1л.', 'Наша сыворотка — это натуральный продукт, полученный в процессе производства сыра. Она богата белками и витаминами, что делает её отличным дополнением к вашему рациону. Используйте сыворотку для приготовления смузи, выпечки или как освежающий напиток.', '4.png', 3, 1, 'Состав: сыворотка, белки, витамины.', NULL, 2),
	(5, 'Молоко козье', 1, 250.0, 180.0, '1л.', 'Козье молоко — это источник легко усваиваемых питательных веществ и витаминов. Оно идеально подходит для людей с непереносимостью коровьего молока. Попробуйте его в чистом виде или добавьте в ваши любимые блюда для получения уникального вкуса.', '5.png', 5, 1, 'Состав: козье молоко, витамины.', NULL, 2),
	(6, 'Сливки питьевые бутылка', 0, 150.0, 150.0, '0,5л', 'Наши питьевые сливки — это идеальный способ добавить кремовую текстуру и богатый вкус в ваши напитки и десерты. Они прекрасно подходят для кофе, чая или как основа для различных кулинарных шедевров.', '6.png', 4, 1, 'Состав: сливки, молоко.', NULL, 2),
	(7, 'Йогурт питьевой', 0, 90.0, 90.0, '0,5л', 'Наш йогурт — это вкусный и полезный продукт, приготовленный из натурального молока с добавлением живых культур. Он богат пробиотиками, которые способствуют здоровью пищеварительной системы. Идеален как для завтрака, так и для перекуса.', '7.png', 2, 1, 'Состав: молоко, закваска, пробиотики.', NULL, 2),
	(8, 'Молоко топленое', 0, 90.0, 90.0, '0,5л', 'Топленое молоко — это традиционный продукт, обладающий насыщенным вкусом и ароматом. Оно проходит специальную обработку, что придаёт ему уникальные свойства. Используйте его для приготовления каш, выпечки или просто наслаждайтесь в чистом виде.', '8.webp', 3, 1, 'Состав: топленое молоко.', NULL, 2),
	(9, 'Мацони', 1, 90.0, 90.0, 'уп.', 'Наш мацони обладает кремовой текстурой и легкой кислинкой, что делает его идеальным как для самостоятельного употребления, так и в качестве ингредиента в различных блюдах. Приготовленный по традиционным рецептам, он не содержит искусственных добавок и консервантов.', '9.png', 3, 1, 'Состав: молоко, закваска.', NULL, 2),
	(10, 'Ряженка', 0, 0.0, 100.0, '0,5л', 'Ряженка — это традиционный кисломолочный продукт, обладающий нежным вкусом и ароматом. Она изготовлена из натурального молока и заквашена специальными культурами, что делает её полезной для пищеварения. Идеально подходит для завтрака или в качестве легкого перекуса.', '10.png', 3, 1, 'Состав: молоко, закваска.', NULL, 2),
	(11, 'Варенец', 0, 100.0, 100.0, '0,5л', 'Варенец — это сладкий кисломолочный напиток, приготовленный из топленого молока. Он обладает насыщенным вкусом и приятной кремовой текстурой. Варенец отлично подходит как для десерта, так и для утоления жажды.', '11.png', 3, 1, 'Состав: топленое молоко, сахар.', NULL, 2),
	(12, 'Творожная масса контейнер(с клубникой)', 0, 90.0, 100.0, '200гр.', 'Творожная масса с клубникой — это вкусное и полезное лакомство, которое сочетает в себе нежный творог и сладкие ягоды. Идеально подходит для завтрака, десертов или как перекус. Наслаждайтесь натуральным вкусом и ароматом свежей клубники.', '12.png', 3, 1, 'Состав: творог, клубника, сахар.', NULL, 3),
	(13, 'Творожная масса контейнер(с ягодами)', 0, 90.0, 100.0, '200гр.', 'Творожная масса с ягодами — это идеальное сочетание нежного творога и разнообразных ягод. Этот продукт не только вкусный, но и полезный, благодаря витаминам и антиоксидантам, содержащимся в ягодах. Отлично подходит для завтрака или десерта.', '13.png', 3, 1, 'Состав: творог, ягоды, сахар.', NULL, 3),
	(14, 'Творожная масса контейнер(шоколадная)', 0, 90.0, 100.0, '200гр.', 'Творожная масса с шоколадом — это сладкое и питательное лакомство, которое порадует любителей шоколада. Нежный творог в сочетании с насыщенным шоколадным вкусом делает этот продукт идеальным для десертов или как перекус.', '14.png', 3, 1, 'Состав: творог, шоколад, сахар.', NULL, 3),
	(16, 'Панакота', 0, 150.0, 120.0, '200гр.', 'Панакота — это итальянский десерт на основе сливок, который обладает нежной текстурой и легким ванильным вкусом. Этот десерт идеально подходит для завершения любого обеда или ужина и может быть подан с различными соусами.', '16.png', 3, 1, 'Состав: сливки, сахар, желатин, ваниль.', NULL, 3),
	(18, 'Панакота шарик стакан', 0, 120.0, 120.0, '200гр.', 'Панакота 2 — это вариация классического итальянского десерта, приготовленная с использованием высококачественных ингредиентов. Нежная текстура и изысканный вкус делают её идеальным выбором для любителей сладкого.', '18.png', 3, 1, 'Состав: сливки, сахар, желатин, ваниль.', NULL, 3),
	(19, 'Панакота с фруктами', 0, 120.0, 120.0, '200гр.', 'Панакота с фруктами — это освежающий десерт, который сочетает в себе нежную панакоту и свежие фрукты. Идеально подходит для летнего угощения или как легкий десерт после обеда.', '19.png', 3, 1, 'Состав: сливки, сахар, фрукты, желатин.', NULL, 3),
	(20, 'Панакота с ягодами', 0, 120.0, 120.0, '200гр.', 'Панакота с ягодами — это изысканный десерт, который сочетает в себе нежную текстуру панакоты и сладкие ягоды. Этот десерт станет отличным завершением любого приема пищи и порадует любителей ягод.', '20.png', 3, 1, 'Состав: сливки, сахар, ягоды, желатин.', NULL, 3),
	(21, 'Сливки сепарированные ведро (3кг)', 0, 1200.0, 1080.0, '3кг.', 'Сепарированные сливки — это высококачественный молочный продукт, полученный путем сепарации молока. Они обладают насыщенным вкусом и идеально подходят для приготовления десертов, соусов и выпечки. Используйте их для создания кремов или как добавку к кофе.', '21.png', 3, 1, 'Состав: сливки.', NULL, 2),
	(22, 'Блины оригинальные с творогом', 0, 300.0, 250.0, 'уп.', 'Блинчики оригинальные с творогом — это вкусное и сытное блюдо, приготовленное из тонкого теста и нежного творога. Они идеально подходят для завтрака или перекуса и могут быть поданы как с сахаром, так и с вареньем.', '22.webp', 3, 1, 'Состав: мука, творог, яйца, сахар.', NULL, 4),
	(23, 'Ватрушка творожная (шоколад/мини)', 0, 380.0, 420.0, 'кг.', 'Творожная ватрушка — это аппетитная выпечка с нежной творожной начинкой и шоколадным вкусом. Она станет отличным дополнением к чаю или кофе и порадует любителей сладкого. Идеально подходит для угощения гостей или как десерт на праздник.', '23.webp', 3, 1, 'Состав: мука, творог, шоколад, сахар.', NULL, 7),
	(24, 'Блины оригинальные с мясом', 0, 300.0, 250.0, 'уп.', 'Наши блинчики имеют тонкую, нежную текстуру и богатую начинку из отборного мяса, приготовленного с добавлением ароматных специй и свежих овощей. Каждый блинчик завернут с любовью и заботой, чтобы вы могли насладиться настоящим домашним вкусом.', '22.webp', 3, 1, 'Состав: мука, мясо, специи, овощи.', NULL, 4),
	(25, 'Масло сливочное (шар 250гр.) ', 0, , 1100.0, 'кг.', 'Наше масло обладает насыщенным кремовым вкусом и нежной текстурой, что делает его идеальным для приготовления различных блюд, выпечки и намазывания на хлеб. Мы используем только натуральные ингредиенты и традиционные методы производства, чтобы сохранить все полезные свойства и вкус.', '25.png', 2, 1, 'Состав: масло, соль.', NULL, 1),
	(26, 'Сыр Адыгейский (перец)', 0, 420.0, 470.0, 'кг.', 'Сыр Адыгейский с добавлением зелени и перца — это нежный и ароматный сыр, который отлично подходит для закусок, салатов и бутербродов. Его уникальный вкус и текстура делают его идеальным дополнением к любому блюду.', '26.png', 2, 1, 'Состав: сыр, перец.', NULL, 6),
	(28, 'Творог топленый', 0, 0.0, 510.0, 'кг.', 'Топленый творог — это натуральный продукт с насыщенным вкусом и кремовой текстурой. Он идеально подходит для приготовления десертов, запеканок или как самостоятельный перекус. Наслаждайтесь его полезными свойствами и вкусом.', '28.png', 2, 1, 'Состав: творог.', NULL, 3),
	(29, 'Сыр Адыгейский оригинальный', 0, 0.0, 470.0, 'кг.', 'Сыр Адыгейский — это мягкий и нежный сыр, который отлично подходит для приготовления различных блюд, закусок и салатов. Его приятный вкус и универсальность делают его популярным выбором на столе.', '29.png', 2, 1, 'Состав: сыр.', NULL, 6),
	(30, 'Творог пластовой (таз)', 0, 0.0, 340.0, 'кг.', 'Пластовой творог — это продукт с нежной текстурой, который идеально подходит для приготовления десертов, запеканок и начинок. Он богат белком и полезными веществами, что делает его отличным выбором для здорового питания.', '30.png', 2, 1, 'Состав: творог.', NULL, 3),
	(31, 'Сырники (уп 450гр.)', 0, 0.0, 230.0, 'уп.', 'Сырники — это популярное блюдо из творога, обжаренное до золотистой корочки. Они идеально подходят для завтрака или десерта и могут подаваться с вареньем, сметаной или медом.', '31.webp', 2, 1, 'Состав: творог, яйца куриные, сахар-песок, мука в/с, соль, ванилин.', NULL, 4),
	(32, 'Чизкейк (киви)', 0, 0.0, 480.0, 'кг.', 'Чизкейк — это нежный и освежающий десерт, который сочетает в себе кремовую текстуру и яркий вкус киви. Идеально подходит для любителей сладкого и фруктового, этот чизкейк станет отличным завершением любого обеда.', '32.png', 2, 1, 'Состав: сливки, сахар, киви, желатин.', NULL, 4),
	(33, 'Чизкейк (клубника)', 0, 0.0, 480.0, 'кг.', 'Чизкейк — это нежный и освежающий десерт, который сочетает в себе кремовую текстуру и сладкий вкус свежей клубники. Идеально подходит для любителей фруктовых десертов, этот чизкейк станет отличным завершением любого обеда или праздника.', '33.png', 2, 1, 'Состав: сливки, сахар, клубника, желатин.', NULL, 4),
	(34, 'Творог весовой вакуум (5 кг)', 0, 0.0, 1750.0, 'уп.', 'Творог ГОСТ в запайке 5 кг — это высококачественный продукт, соответствующий всем стандартам. Он обладает нежной текстурой и приятным вкусом, идеально подходит для приготовления различных блюд, десертов и как самостоятельный перекус.', '34.png', 2, 1, 'Состав: творог.', NULL, 3),
	(35, 'Творог весовой вакуум (3 кг)', 0, 0.0, 1050.0, 'уп.', 'Творог ГОСТ в запайке 3 кг — это натуральный продукт, который идеально подходит для приготовления запеканок, сырников и других блюд. Его высокое качество и вкус делают его отличным выбором для здорового питания.', '35.png', 2, 1, 'Состав: творог.', NULL, 3),
	(36, 'Сыр Адыгейский стакан (650гр)', 0, 500.0, 480.0, 'уп.', 'Сыр Адыгейский (700 гр) — это мягкий и нежный сыр, который отлично подходит для закусок, салатов и приготовления различных блюд. Его приятный вкус и универсальность делают его популярным выбором на столе.', '36.png', 2, 1, 'Состав: сыр.', NULL, 6),
	(37, 'Сыр Адыгейский (зелень) стакан (650гр)', 0, 0.0, 470.0, 'уп.', 'Сыр Адыгейский с зеленью и перцем (700 гр) — это ароматный и вкусный сыр, который станет отличным дополнением к вашим блюдам. Его уникальный вкус и текстура делают его идеальным для закусок и салатов.', '37.png', 2, 1, 'Состав: сыр, зелень, перец.', NULL, 6),
	(38, 'Сметана ГОСТ ведро (3кг)', 0, 0.0, 1050.0, '3кг.', 'Сметана ГОСТ — это натуральный молочный продукт с богатым вкусом и кремовой текстурой. Она идеально подходит для заправки салатов, приготовления соусов и как добавка к различным блюдам.', '21.png', 2, 1, 'Состав: сметана.', NULL, 3),
	(39, 'Желе (ваниль-клубника)', 0, 0.0, 120.0, 'уп.', 'Желе ваниль с клубникой — это сладкий и ароматный десерт, который сочетает в себе кремовую ваниль и сладость клубники, создавая нежный и освежающий вкус.', '39.png', 2, 1, 'Состав: желатин, сахар, клубника, ваниль.', NULL, 5),
	(40, 'Желе (ваниль-малина)', 0, 0.0, 120.0, 'уп.', 'Желе ваниль с малиной — это сладкий и ароматный десерт, который сочетает в себе кремовую ваниль и кислинку малины, создавая гармоничный и освежающий вкус.', '39.png', 2, 1, 'Состав: желатин, сахар, малина, ваниль.', NULL, 5),
	(41, 'Желе (манго-малина)', 0, 0.0, 120.0, 'уп.', 'Желе манго с малиной — это освежающий десерт, который сочетает в себе сладость манго и кислинку малины.', '50.png', 2, 1, 'Состав: манго, малина, сахар, вода, желатин.', NULL, 5),
	(42, 'Желе (киви-клубника)', 0, 0.0, 120.0, 'уп.', 'Желе киви с клубникой — это освежающий десерт, который сочетает в себе сладость клубники и кислинку киви.', '41.png', 2, 1, 'Состав: киви, клубника, сахар, вода, желатин.', NULL, 5),
	(43, 'Запеканка со смородиной', 0, 0.0, 450.0, 'кг.', 'Запеканка со смородиной — это нежный десерт с ярким вкусом смородины.', '42.png', 2, 1, 'Состав: смородина, творог, яйца, сахар, мука.', NULL, 8),
	(44, 'Запеканка (персик)', 0, 0.0, 450.0, 'кг.', 'Запеканка с персиками — это сладкий и ароматный десерт, который порадует вас своим вкусом.', '44.webp', 2, 1, 'Состав: персики, творог, яйца, сахар, мука.', NULL, 8),
	(45, 'Запеканка микс, персик-вишня', 0, 0.0, 450.0, 'кг.', 'Запеканка с персиком и вишней — это идеальное сочетание сладости и кислинки.', '44.png', 2, 1, 'Состав: персики, вишня, творог, яйца, сахар, мука.', NULL, 8),
	(46, 'Запеканка микс, персик-клубника', 0, 0.0, 450.0, 'кг.', 'Запеканка с персиком и клубникой — это вкусный десерт, который понравится всем.', '45.png', 2, 1, 'Состав: персики, клубника, творог, яйца, сахар, мука.', NULL, 8),
	(47, 'Запеканка микс, малина-манго-маракуйя', 0, 0.0, 450.0, 'кг.', 'Запеканка с малиной и ???? — это интересный десерт с неожиданным вкусом.', '46.webp', 2, 1, 'Состав: малина, ????, творог, яйца, сахар, мука.', NULL, 8),
	(48, 'Запеканка "сердечки" микс. киви-клубника-персик', 0, 0.0, 450.0, 'кг.', 'Запеканка "сердечки" с киви и клубникой — это оригинальный десерт для особых случаев.', '47.png', 2, 1, 'Состав: киви, клубника, ????, творог, яйца, сахар, мука.', NULL, 8),
	(49, 'Запеканка микс. смород.-клубн.-виш.-мал.', 0, 0.0, 450.0, 'кг.', 'Запеканка с миксом ягод — это насыщенный вкус и аромат.', '48.webp', 2, 1, 'Состав: смородина, клубника, вишня, малина, творог, яйца, сахар, мука.', NULL, 8),
